CREATE TABLE `account`
(
    `id`           BIGINT auto_increment PRIMARY KEY,
    `username`     VARCHAR(20),
    `email`        VARCHAR(50),
    `password`     VARCHAR(255),
    `status`       INTEGER,
    `version`      INTEGER,
    `date_created` TIMESTAMP(3),
    `last_updated` TIMESTAMP(3)
);

CREATE INDEX idx_username ON `account` (username);
CREATE INDEX idx_email ON `account` (email);

CREATE TABLE `comment`
(
    `id`           BIGINT auto_increment PRIMARY KEY,
    `aid`          BIGINT,
    `pid`          BIGINT,
    `content`      TEXT,
    `depth`        INTEGER,
    `status`       INTEGER,
    `version`      INTEGER,
    `date_created` TIMESTAMP(3),
    `last_updated` TIMESTAMP(3)
);
CREATE INDEX idx_aid ON `comment` (aid);
CREATE INDEX idx_created ON `comment` (date_created);

